def firstA (string):
    c = 0
    for i in range(0, len(string)):
        if (string[i] == "("):
            c = c+1
        elif (string[i] == ")"):
            c = c-1
        else:
            print (string[i])
    print (c)

def firstB (string, floor):
    c = 0
    for i in range(0, len(string)):
        if (string[i] == "("):
            c = c+1
        elif (string[i] == ")"):
            c = c-1
        else:
            print (string[i])
        if c == floor:
            print (i+1)
